keypad = {
  (0,0):"1",
  (0,1):"2",
  (0,2):"3",
  (1,0):"4",
  (1,1):"5",
  (1,2):"6",
  (2,0):"7",
  (2,1):"8",
  (2,2):"9"
}

keypad2 = {
  (0,2):"1",
  (1,1):"2",
  (1,2):"3",
  (1,3):"4",
  (2,0):"5",
  (2,1):"6",
  (2,2):"7",
  (2,3):"8",
  (2,4):"9",
  (3,1):"A",
  (3,2):"B",
  (3,3):"C",
  (4,2):"D"
}

move_dict = {'U':(-1,0),'R':(0,1),'D':(1,0),'L':(0,-1)}

def moveOnKeypad(move,pos):
  return (pos[0]+move[0],pos[1]+move[1])


def passwordInterpreter(some_input):
  inputs = [[s for s in l] for l in some_input.split('\n')]
  pos = (2,0)
  pw = ''
  for move_set in inputs:
    print("Starting new sequence!")
    for move in move_set:
      if moveOnKeypad(move_dict[move],pos) in keypad2:
        print("Oh yeah:",move)
        pos = moveOnKeypad(move_dict[move],pos)
        print(pos)
      else:
        print('Oh no:',move)
    pw+=str(keypad2[pos])
  return pw




